import React from 'react';
import './Courses.css'; // Подключаем файл стилей для компонента Courses
import 'bootstrap/dist/css/bootstrap.min.css'; // Импорт стилей CSS
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { Modal, Button } from 'react-bootstrap';

const Courses = () => {
    const [showModal, setShowModal] = useState(false);

    const handleShowModal = () => setShowModal(true);
    const handleCloseModal = () => setShowModal(false);

    return (
        <div className="container">
            {/* ... */}
            <table className="table table-bordered table-hover custom-table">
                <tbody>
                <tr>
                    <td onClick={handleShowModal}>Английский</td>
                    {/* ... */}
                </tr>
                {/* Остальные строки таблицы */}
                </tbody>
            </table>
            {/* Модальное окно */}
            <Modal show={showModal} onHide={handleCloseModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Английский</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p>Основной язык общения в мире бизнеса, технологий и культуры.</p>
                    <p>Цена: 20000 тенге</p>
                    {/* Другая информация */}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>
                        Закрыть
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default Courses;
