import React, {useRef, useState} from 'react';
import './AudioPlayer.css'
const AudioPlayer = () => {
    const audioFiles = [
        '/Ed-Sheeran-Shape-of-You-Lyrics.mp3',
        '/BAD-OMENS-What-It-Cost-Like-A-Villain-Lyrics-Video.mp3',
        '/audio_file_3.mp3',
        // Добавьте пути к вашим аудиофайлам
    ];
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const audioRef = useRef(new Audio(audioFiles[currentTrackIndex]));
    const [isPlaying, setIsPlaying] = useState(false);

    useEffect(() => {
        audioRef.current.src = audioFiles[currentTrackIndex];
        if (isPlaying) {
            audioRef.current.play();
        } else {
            audioRef.current.pause();
        }
    }, [currentTrackIndex, isPlaying]);

    const playAudio = () => {
        setIsPlaying(true);
    };

    const pauseAudio = () => {
        setIsPlaying(false);
    };

    const nextAudio = () => {
        if (currentTrackIndex < audioFiles.length - 1) {
            setCurrentTrackIndex(currentTrackIndex + 1);
        } else {
            setCurrentTrackIndex(0);
        }
    };

    const prevAudio = () => {
        if (currentTrackIndex > 0) {
            setCurrentTrackIndex(currentTrackIndex - 1);
        } else {
            setCurrentTrackIndex(audioFiles.length - 1);
        }
    };


    return (
        <div className="audio-player">
            <audio controls src={audioFiles[currentTrackIndex]} type="audio/mpeg">
                Ваш браузер не поддерживает аудио тег.
            </audio>
            <div className="audio-controls">
                <button onClick={pauseAudio}>Pause</button>
                <button onClick={nextAudio}>Next</button>
                <button onClick={prevAudio}>Prev</button>
            </div>
        </div>
    );
};

export default AudioPlayer;
