import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import './Courses.css'; // Подключаем файл стилей для компонента Courses
import 'bootstrap/dist/css/bootstrap.min.css'; // Импорт стилей CSS

const Courses = () => {
    const headingClass = "course-heading"; // Класс для заголовка курсов
    const [showModal, setShowModal] = useState(false);
    const [selectedCourse, setSelectedCourse] = useState({
        name: '',
        description: '',
        price: ''
    });

    const handleShowModal = (name, description, price) => {
        setSelectedCourse({ name, description, price });
        setShowModal(true);
    };

    const handleCloseModal = () => setShowModal(false);

    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-md-8">
                    <div className="center-content">
                        <h1 className={headingClass}>Наши языки</h1>
                        {/* Таблица с курсами */}
                        <table className="table table-bordered table-hover custom-table">
                            <caption>Курсы по изучению языков</caption>
                            <thead className="thead-dark">
                            <tr>
                                <th>Язык</th>
                                <th>Описание</th>
                                <th>Цена</th>
                            </tr>
                            </thead>
                            <tbody>
                            <tr onClick={() => handleShowModal('Английский', 'Основной язык общения в мире бизнеса, технологий и культуры.', '20000 тенге')}>
                                <td>Английский</td>
                                <td>Основной язык общения в мире бизнеса, технологий и культуры.</td>
                                <td>20000 тенге</td>
                            </tr>
                            {/* Добавьте обработчики событий для других строк */}
                            {/* ... */}
                            </tbody>
                        </table>

                        {/* Модальное окно */}
                        <Modal show={showModal} onHide={handleCloseModal}>
                            <Modal.Header closeButton>
                                <Modal.Title>{selectedCourse.name}</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                <p>{selectedCourse.description}</p>
                                <p>Цена: {selectedCourse.price}</p>
                                {/* Другая информация */}
                            </Modal.Body>
                            <Modal.Footer>
                                <Button variant="secondary" onClick={handleCloseModal}>
                                    Закрыть
                                </Button>
                            </Modal.Footer>
                        </Modal>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Courses;
