import React, { useState } from 'react';
import './AudioPlayer.css'
const AudioPlayer = () => {
    const audioFiles = [
        '/Ed-Sheeran-Shape-of-You-Lyrics.mp3',
        '/BAD-OMENS-What-It-Cost-Like-A-Villain-Lyrics-Video.mp3',
        '/audio_file_3.mp3',
        // Добавьте пути к вашим аудиофайлам
    ];

    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const currentAudio = new Audio(audioFiles[currentTrackIndex]);
    const prevAudio = () => {
        let prevTrackIndex = currentTrackIndex - 1;
        if (prevTrackIndex < 0) {
            prevTrackIndex = audioFiles.length - 1;
        }
        setCurrentTrackIndex(prevTrackIndex);
        currentAudio.src = audioFiles[prevTrackIndex];
        currentAudio.currentTime = 0; // Установка времени воспроизведения в начало трека
        currentAudio.play();
    };

    const pauseAudio = () => {
        currentAudio.pause();
    };

    const nextAudio = () => {
        let nextTrackIndex = currentTrackIndex + 1;
        if (nextTrackIndex >= audioFiles.length) {
            nextTrackIndex = 0;
        }
        if (currentAudio) {
            currentAudio.pause();
            currentAudio.currentTime = 0;
        }
        setCurrentTrackIndex(nextTrackIndex);
        const newAudio = new Audio(audioFiles[nextTrackIndex]);
        setCurrentAudio(newAudio);
        newAudio.play();
    };



    return (
        <div className="audio-player">
            <audio controls src={audioFiles[currentTrackIndex]} type="audio/mpeg">
                Ваш браузер не поддерживает аудио тег.
            </audio>
            <div className="audio-controls">
                <button onClick={pauseAudio}>Pause</button>
                <button onClick={nextAudio}>Next</button>
                <button onClick={prevAudio}>Prev</button>
            </div>
        </div>
    );
};

export default AudioPlayer;
