import React, { useState } from 'react';
import './Dropdown.css'; // Подключите файл CSS

const Dropdown = () => {
    const [activeIndex, setActiveIndex] = useState(null);

    const questions = [
        // Ваши вопросы и ответы
    ];

    const handleToggle = (index) => {
        if (activeIndex === index) {
            setActiveIndex(null);
        } else {
            setActiveIndex(index);
        }
    };

    const splitQuestions = (start, end) => {
        return questions.slice(start, end).map((item, index) => (
            <div key={index} className="question-wrapper">
                <div className="question" onClick={() => handleToggle(start + index)}>
                    {item.question}
                </div>
                {activeIndex === start + index && (
                    <div className="answer">
                        <p>{item.answer}</p>
                    </div>
                )}
            </div>
        ));
    };

    const midpoint = Math.ceil(questions.length / 2);

    return (
        <div className="dropdown-container">
            <div className="column">
                {splitQuestions(0, midpoint)}
            </div>
            <div className="column">
                {splitQuestions(midpoint, questions.length)}
            </div>
        </div>
    );
};

export default Dropdown;
