import React, { useState } from 'react';
import './Dropdown.css'; // Подключите файл CSS

const Dropdown = () => {
    const [activeIndex, setActiveIndex] = useState(null);

    const questions = [
        {
            question: 'Почему стоит учить английский сейчас?',
            answer:
                'Изучение английского языка - это отличный способ открыть для себя новые возможности и лучше понять окружающий мир. Английский является основным языком бизнеса, образования и путешествий, и он приобретает все большее значение в нашем все более глобализированном мире. Английский язык - это также отличный способ завести новых друзей и наладить отношения с людьми из других стран. Знание английского языка позволит вам понимать и общаться с людьми из других стран, а также поможет вам использовать многие интернет-сайты и форумы. Знание английского языка может значительно повысить ваши шансы на работу и помочь вам успешно сотрудничать с людьми из разных стран. Наша школа предлагает успешное обучение английскому языку в Алматы. У нас есть 8 филиалов в городе Алматы, а это еще не предел. Мы преподаем детей от 6 лет, подростков и взрослых. Наша миссия - обеспечить качественное обучение по доступной цене. При этом наши ученики получают результаты в комфортном для них темпе.',
        },
        // Другие вопросы и ответы по аналогии
    ];

    const handleToggle = (index) => {
        if (activeIndex === index) {
            setActiveIndex(null);
        } else {
            setActiveIndex(index);
        }
    };

    return (
        <div className="dropdown-container">
            {questions.map((item, index) => (
                <div key={index} className="question-wrapper">
                    <div className="question" onClick={() => handleToggle(index)}>
                        {item.question}
                    </div>
                    {activeIndex === index && (
                        <div className="answer">
                            <p>{item.answer}</p>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default Dropdown;
