import React, { useState, useRef } from 'react';

import './SchoolInfoBox.css'


const SchoolInfoBox = () => {
    const [scrollPosition, setScrollPosition] = useState(0);
    const scrollRef = useRef(null);

    useEffect(() => {
        const handleScroll = () => {
            if (scrollRef.current) {
                const position = scrollRef.current.scrollLeft;
                setScrollPosition(position);
            }
        };

        if (scrollRef.current) {
            scrollRef.current.addEventListener('scroll', handleScroll);
        }

        return () => {
            if (scrollRef.current) {
                scrollRef.current.removeEventListener('scroll', handleScroll);
            }
        };
    }, []);

    const scrollTo = (position) => {
        if (scrollRef.current) {
            scrollRef.current.scrollTo({
                left: position,
                behavior: 'smooth',
            });
        }
    };


    return (
        <div className="school-info-box">
            <h2>Что вас ждет в нашей школе</h2>
            <nav className="bottom-navigation" ref={scrollRef} onScroll={handleScroll}>
                <ul className="scroll-list">
                    <li>
                        <a href="/speaking-clubs">
                            <img
                                src="https://softteco.team/wp-content/uploads/2022/03/English_speaking_club_oblojka.png"
                                alt="Speaking Clubs"
                                className="event-image" // Добавляем класс к изображению
                            />
                            Speaking Clubs
                        </a>
                    </li>
                    <li>
                        <a href="/super-travel-events">
                            <img
                                src="https://us.travelctm.com/wp-content/uploads/2019/10/Double-Image-Incentive-600x438.jpg"
                                alt="Super Travel Events"
                                className="event-image" // Добавляем класс к изображению
                            />
                            Super Travel Events
                        </a>
                    </li>
                    <li>
                        <a href="/native-meeting">
                            <img
                                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcROxMy6LHVRBoYo6dRm6Ax2mvYfALw8w8nE8uk5oKgLUa5lCQwC_HwcaDUPIVJDpxZhaO8&usqp=CAU"
                                alt="Native Meeting"
                                className="event-image" // Добавляем класс к изображению
                            />
                            Native Meeting
                        </a>
                    </li>
                    {/* Добавьте больше элементов при необходимости */}
                </ul>
            </nav>
            <div className="markers">
                <span className={scrollPosition >= 0 ? 'active' : ''} onClick={() => scrollTo(0)}></span>
                <span className={scrollPosition >= 200 ? 'active' : ''} onClick={() => scrollTo(200)}></span>
                <span className={scrollPosition >= 400 ? 'active' : ''} onClick={() => scrollTo(400)}></span>
                {/* Adjust marker positions based on scroll width */}
            </div>
        </div>
    );
};

export default SchoolInfoBox;
