import React, { useState, useRef } from 'react';
import './AudioPlayer.css'; // Импортируйте ваш CSS файл здесь

function AudioPlayer() {
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const audioPlayer = useRef(null);

    const tracks = [
        // Вставьте ваш массив треков здесь, он остаётся таким же
    ];

    const playPreviousTrack = () => {
        setCurrentTrackIndex((prevIndex) => (prevIndex - 1 + tracks.length) % tracks.length);
        setTrack(currentTrackIndex);
        togglePlay();
    };

    const playNextTrack = () => {
        setCurrentTrackIndex((prevIndex) => (prevIndex + 1) % tracks.length);
        setTrack(currentTrackIndex);
        togglePlay();
    };

    const setTrack = (index) => {
        const currentTrack = tracks[index];
        audioPlayer.current.src = currentTrack.trackURL;
        updateTrackInfo();
    };

    const updateTrackInfo = () => {
        const currentTrack = tracks[currentTrackIndex];
        document.getElementById('trackName').textContent = currentTrack.name;
        document.getElementById('artist').textContent = currentTrack.artist;
        document.getElementById('albumCover').src = currentTrack.albumCoverURL;
        // Обновите другую информацию о треке, если необходимо
    };

    const togglePlay = () => {
        if (isPlaying) {
            audioPlayer.current.pause();
        } else {
            audioPlayer.current.play();
        }
        setIsPlaying(!isPlaying);
    };

    const handleVolumeChange = (e) => {
        const volume = e.target.value;
        audioPlayer.current.volume = volume / 100;
    };

    const handleProgressChange = (e) => {
        const progress = e.target.value;
        const duration = audioPlayer.current.duration;
        const seekTime = (duration * progress) / 100;
        audioPlayer.current.currentTime = seekTime;
    };

    return (
        <div className="music-player">
            <div className="player-controls">
                <button onClick={playPreviousTrack}>Prev</button>
                <button id="playBtn" onClick={togglePlay}>
                    {isPlaying ? 'Pause' : 'Play'}
                </button>
                <button onClick={playNextTrack}>Next</button>
                <input
                    type="range"
                    id="volumeSlider"
                    min="0"
                    max="100"
                    step="1"
                    onChange={handleVolumeChange}
                />
                <div id="trackInfo">
                    <img id="albumCover" src="/music/cover.jpg" alt="Album Cover" />
                    <div id="songDetails">
                        <p id="trackName"></p>
                        <p id="artist"></p>
                    </div>
                </div>
                <input
                    type="range"
                    id="progressSlider"
                    min="0"
                    max="100"
                    step="1"
                    onChange={handleProgressChange}
                />
                <audio id="audioPlayer" ref={audioPlayer} src=""></audio>
            </div>
        </div>
    );
}

export default AudioPlayer;
